package javaconcepts;

public class stringarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s[]=new String[3];
		s[0]="shravani";
		s[1]="apple";
		s[2]="mango";
		System.out.println(s.length);
		for(String a:s) {
			System.out.println(a);
		}

	}

}
